mapper.select:
/* commonMapper.selectAllMenu */ SELECT M.MENU_UID AS menuUid , NVL(M.PARENT_MENU_UID, '') AS parentMenuUid , M.DEPTH AS depth , M.`ORDER` AS `order` , M.TITLE AS title , M.PATH AS path , M.REGISTRATION_DATE AS registrationDate , M.REGISTRATION_USER AS registrationUser , M.UPDATE_DATE AS updateDate , M.UPDATE_USER AS updateUser , M.FLAG_ACTIVATED AS flagActivated , M.FLAG_DELETED AS flagDeleted
, CASE
WHEN M.PATH LIKE CONCAT('/', #{subPath}, '%') THEN 'Y'
ELSE 'N' END AS flagSelected
FROM MENU M INNER JOIN USER U ON M.ACCESS_LEVEL >= U.ACCESS_LEVEL WHERE M.FLAG_DELETED = 'N' AND M.FLAG_ACTIVATED = 'Y' AND U.USER_UID = #{userUid};
