mapper.select:
/* loginMapper.selectUser */ SELECT USER_UID AS userUid , PASSWORD AS password , USER_NAME AS userName , ARCHIVE_NAME AS archiveName , CONCAT('/', ARCHIVE_NAME) AS myArchiveUrlPath , CONCAT(GET_ARCHIVE_LOGO(USER_UID), ' Admin') AS archiveLogo FROM USER WHERE
USER_UID = #{userUid} AND FLAG_DELETED = 'N'
EMAIL = #{email} AND FLAG_DELETED = 'N'
