mapper.select:
/* optionMapper.selectCategory */ WITH RECURSIVE CATEGORY_TREE AS ( SELECT C1.CATEGORY_UID , C1.CATEGORY_NAME , C1.DEPTH , CAST(LPAD(C1.`ORDER`, 5, '0') AS CHAR(255)) AS SORT_PATH , C1.FLAG_DELETED FROM CATEGORY C1 WHERE C1.PARENT_CATEGORY_UID IS NULL AND C1.USER_UID = #{userUid}
UNION ALL
SELECT C2.CATEGORY_UID , C2.CATEGORY_NAME , C2.DEPTH , CONCAT(CT.SORT_PATH, '-', LPAD(C2.`ORDER`, 5, '0')) , C2.FLAG_DELETED FROM CATEGORY C2 INNER JOIN CATEGORY_TREE CT ON C2.PARENT_CATEGORY_UID = CT.CATEGORY_UID AND C2.USER_UID = #{userUid} ) SELECT CATEGORY_UID AS codeKey , CATEGORY_UID AS codeValue , CATEGORY_NAME AS codeLabel FROM CATEGORY_TREE WHERE DEPTH = 2 AND FLAG_DELETED = 'N' ORDER BY SORT_PATH
